const React = require('react');

function main(o) {
    const req = o.useRemote("input");
    let t = null;
    if (req.loading)
        t = <span>...</span>
    
    if (req.error)
        t = <span>oops...</span>

    if (req.data)
        t = <span>{req.data}</span>
    
    return (
        <div>
            {t}
            <button onClick={req.refresh}>Refresh</button>
        </div>
    );
}

module.exports = { main };
